import React from "react";

class Billpage extends React.Component {
  constructor() {
    super();
    this.state = {
      amt: 0
    };
  }
  change = e => {
    this.setState({ amt: +e.target.value });
  };
  purchase = () => {
    console.log("purchased");
  };
  Cancelpurchase = () => {
    console.log("cancelled...");
  };
  render() {
    console.log(this.props.location.state.item);
    return (
      <div className="row">
        <div className="col s12 m7 l7">
          <div style={{ backgroundColor: "grey" }}>
            {this.props.location.state.all.map((i, index) => {
              return (
                <div key={index}>
                  {i} => {this.props.location.state.item[i].req} Rs.
                  {this.props.location.state.item[i].totPrice}
                </div>
              );
            })}
            Total {this.props.location.state.bill}
          </div>
          <br />
          <button onClick={this.purchase}>Continue</button>
          <button onClick={this.Cancelpurchase}>Cancel</button>
        </div>
        <div className="col s11 m4 l4 offset-s1 m1 l1">
          products Price:
          <input
            type="number"
            disabled
            value={this.props.location.state.bill}
          />
          Given Price:
          <input
            type="text"
            autoFocus
            value={this.state.amt}
            onChange={this.change}
          />
          Remaining :
          <input
            type="number"
            disabled
            value={this.props.location.state.bill - this.state.amt}
          />
        </div>
      </div>
    );
  }
}
export default Billpage;
