import React from "react";
import { fetch } from "../Fetches";
import ItemList from "./ItemList";
import SelectedItemList from "./Selected_ItemList";
import { Link } from "react-router-dom";

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      selected_items: [],
      items: [],
      totPrice: 0,
      isloading: "Loading..."
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push({ ...doc.data(), id: doc.id });
    });
    this.state.products && this.setState({ isloading: "" });
  };
  change = async e => {
    let name = e.target.id;
    let value = e.target.value;
    let a = Object.assign({}, this.state.selected_items, {
      ...this.state.selected_items,
      [name]: {
        ...this.state.selected_items[name],
        req: +value,
        totPrice:
          +this.state.products
            .filter(i => i.Name === name)
            .map(i => i.Price)[0] * value
      }
    });
    console.log(a);
    await this.setState({ selected_items: a });
    await this.tot();
  };
  add = async e => {
    let a = e.target.id;
    if (!this.state.items.includes(a)) {
      console.log("Added", e.target.id);
      this.setState(prev => {
        return {
          selected_items: {
            ...prev.selected_items,
            [this.state.products
              .filter(i => i.Name === a)
              .map(i => i.Name)[0]]: {
              req: 1,
              totPrice: +this.state.products
                .filter(i => i.Name === a)
                .map(i => i.Price)[0],
              price: +this.state.products
                .filter(i => i.Name === a)
                .map(i => i.Price)[0]
            }
          }
        };
      });
      await this.setState({
        items: [...this.state.items, a]
      });
    }
    await this.tot();
  };
  tot = () => {
    let a =
      this.state.items.length !== 0
        ? this.state.items
            .map(i => {
              return this.state.selected_items[i].totPrice;
            })
            .reduce(this.summation)
        : 0;
    this.setState({ totPrice: a });
  };
  summation = (a, b) => a + b;
  render() {
    return (
      <div className="row">
        <div className="col s12 m6 l6">
          <h5>{this.state.isloading}</h5>
          {this.state.isloading !== "Loading..." &&
            this.state.products.map(i => {
              return <ItemList item={i} key={i.Name} add={this.add} />;
            })}
        </div>
        <div
          className="col s12 m6 l6"
          style={{
            overflow: "auto",
            backgroundColor: "rgb(213, 237, 218)",
            minHeight: "100%",
            height: 520
          }}
        >
          <table>
            <tbody>
              {this.state.items &&
                this.state.items.map((i, index) => {
                  return (
                    <SelectedItemList
                      item={this.state.selected_items[i]}
                      name={i}
                      change={this.change}
                      key={index}
                    />
                  );
                })}
            </tbody>
          </table>
          <div style={{ position: "fixed", bottom: 0, width: "40%" }}>
            {this.state.selected_items.length !== 0 ? (
              <div className="row">
                <div className="col s12 m6 l8">
                  <Link
                    to={{
                      pathname: "/billing...",
                      state: {
                        item: this.state.selected_items,
                        bill: this.state.totPrice,
                        all: this.state.items
                      }
                    }}
                  >
                    <button className="waves-effect waves-light btn">
                      Bill it!
                    </button>
                  </Link>
                </div>
                <div className="col s12 m6 l4">
                  <h5>Grand Total :{this.state.totPrice}</h5>
                </div>
              </div>
            ) : null}
          </div>
        </div>
        <br />
      </div>
    );
  }
}

export default Dashboard;
