import React from "react";
import { fetch } from "../Fetches";
import ItemList from "./ItemList";
import SelectedItemList from "./Selected_ItemList";
import { Link } from "react-router-dom";

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      selected_items: [],
      items: [],
      totPrice: 0,
      isloading: "Loading..."
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push({ ...doc.data(), id: doc.id });
    });
    this.state.products && this.setState({ isloading: "" });
  };
  change = async e => {
    let name = e.target.id;
    let value = e.target.value;
    if(value === 0){
      console.log(0 ,"item")
    }
    let a = Object.assign({}, this.state.selected_items, {
      ...this.state.selected_items,
      [name]: {
        ...this.state.selected_items[name],
        req: +value,
        totPrice:
          +this.state.products
            .filter(i => i.Name === name)
            .map(i => i.Price)[0] * value
      }
    });
    console.log(a);
    await this.setState({ selected_items: a });
    await this.tot();
  };
  add = async e => {
    let a = e.target.id;
    let item = this.state.products.filter(i => i.Name === a);
    if (!this.state.items.includes(a)) {
      console.log("Added", e.target.id);
      this.setState(prev => {
        return {
          selected_items: {
            ...prev.selected_items,
            [a]: {
              req: 1,
              totavail: +item.map(i => i.Avail)[0],
              id: this.state.products
                .filter(i => i.Name === a)
                .map(i => i.id)[0],
              totPrice: +item.map(i => i.Price)[0],
              price: +item.map(i => i.Price)[0]
            }
          }
        };
      });
      await this.setState({
        items: [...this.state.items, a]
      });
      await this.tot();
    }
  };
  tot = () => {
    console.log("in tot");
    let a =
      this.state.items.length !== 0
        ? this.state.items
            .map(i => {
              return this.state.selected_items[i].totPrice;
            })
            .reduce(this.summation)
        : 0;
    this.setState({ totPrice: a });
  };
  delete = async e => {
    let name = e.target.id;
    let res = Object.assign({}, this.state.selected_items);
    console.log(res);
    delete res[name];
    console.log(res);
    await this.setState({ items: this.state.items.filter(i => i !== name) });
    await this.setState({ selected_items: res });
    await this.tot();
  };
  summation = (a, b) => a + b;
  render() {
    return (
      <div>
        {this.state.isloading === "" ? (
          <div className='row'>
            {this.state.selected_items.length !== 0
              ? console.log(this.state.selected_items)
              : null}
            <div className='col s12 m6 l7'>
              <div className='row'>
                {this.state.products.map(i => {
                  return <ItemList item={i} key={i.Name} add={this.add} />;
                })}
              </div>
            </div>
            <div
              className='col s12 m6 l5 z-depth-5 '
              style={{
                overflow: "auto",
                marginTop:"1%",
                minHeight: "100%",
                height: 520
              }}
            >
              <div>
                {this.state.items &&
                  this.state.items.map((i, index) => {
                    return (
                      <SelectedItemList
                        item={this.state.selected_items[i]}
                        name={i}
                        change={this.change}
                        key={index}
                        delt={this.delete}
                      />
                    );
                  })}
              </div>
          <div className="container">
          <div style={{ position: "fixed", bottom: 0, width: "40%" }}>
                {this.state.items.length !== 0 ? (
                  <div className='row'>
                    <div className='col s12 m6 l6' style={{marginBottom:0}} >
                      <Link
                        to={{
                          pathname: "/billing...",
                          state: {
                            item: this.state.selected_items,
                            bill: this.state.totPrice,
                            all: this.state.items
                          }
                        }}
                      >
                      <button className='waves-effect waves-light btn'>
                        Bill it!
                      </button>
                      </Link>
                    </div>
                    <div className='col s12 m6 l6'>
                      <h5>Grand Total :{this.state.totPrice}</h5>
                    </div>
                  </div>
                ) : null}
              </div>
          </div>
            </div>
            <br />
          </div>
        ) : (
          <React.Fragment>
          <div className='progress'>
            <div className='indeterminate' />
          </div>
          <h1 style={{textAlign:'center'}} >Loading....</h1>
          </React.Fragment>
        )}
      </div>
    );
  }
}

export default Dashboard;
