import React from "react";
import { fetch } from "../Fetches";
import ItemList from "./ItemList";
import SelectedItemlist from "./Selected_ItemList";

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      selected_items: [],
      totPrice: 0,
      isloading: "Loading..."
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push(doc.data());
    });
    this.state.products && this.setState({ isloading: "Done" });
  };
  change = e => {
    let id = e.target.id;
    let a = this.state.selected_items.filter(i => i.name === id)[0];
    consthis.setState({
        req:a.req+1
    })
  };
  add = e => {
    console.log("Adding...", e.target.id);
    let a = e.target.id;
    if (!this.state.selected_items.map(i => i.name).includes(a)) {
      this.setState(prev => {
        return {
          selected_items: [
            ...prev.selected_items,
            {
              name: a,
              req: 1,
              totPrice: +this.state.products
                .filter(i => i.Name === a)
                .map(i => i.Price)[0]
            }
          ]
        };
      });
    }
  };
  render() {
    return (
      <div className="row">
        <div className="col s12 m6 l6">
          <h5>{this.state.isloading}</h5>
          {console.log(this.state.selected_items)}
          {this.state.isloading === "Done" &&
            this.state.products.map(i => {
              return <ItemList item={i} key={i.Name} add={this.add} />;
            })}
        </div>
        <div className="col s12 m6 l6">
          <h5>Selected_items</h5>
          <table>
            <tbody>
              {this.state.selected_items &&
                this.state.selected_items.map(i => {
                  return (
                    <SelectedItemlist
                      item={i}
                      key={i.name}
                      change={this.change}
                    />
                  );
                })}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default Dashboard;
