import React from "react";
import { fetch } from "./Fetches";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      products: [],
      isloading: "Loading...."
    };
  }
  componentDidMount() {
    // console.log(this.state);
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push(doc.data());
    });
    await this.setState({ isloading: "Done" });
  };
  add = () => {
    console.log("Adding...");
  };
  render() {
    return (
      <div className="row">
        <div className="col s6 m8 l8">
          {console.log(this.state)}
          <h5>{this.state.isloading}</h5>
          {this.state.isloading === "Done" &&
            this.state.products.map(i => {
              return (
                <div onClick={this.add} key={i.Name}>
                  {i.Name} {i.Avail} @{i.Price}
                </div>
              );
            })}
        </div>
        <div className="col "></div>
      </div>
    );
  }
}

export default App;
