import React from 'react';
import { Link } from 'react-router-dom';

class ItemList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      display: false
    };
  }
  render() {
    return (
      <span
        className='col s12 m6 l6 '
        id={this.props.item.Name}
        onClick={this.props.add}
      >
        <span
          className={
            this.props.item.Avail >= 10
              ? 'card hoverable waves-effect waves-block waves-red'
              : this.props.item.Avail === 0
              ? 'card waves-effect waves-block waves-grey grey lighten-1'
              : 'card hoverable waves-effect waves-block waves-red red lighten-4'
          }
          style={{ minHeight: 100 }}
        >
          <div
            className='card'
            style={{
              marginLeft: 10,
              position: 'absolute',
              height: 90,
              width: 90,
              borderRadius: 50,
              textAlign: 'center'
            }}
          >
            <div style={{ marginTop: 20 }}>
              <b>Avail</b>
              <br />
              <Link
                to={{
                  pathname: '/edit/' + this.props.item.id,
                  state: this.props.item
                }}
              >
                <span className='center'>{this.props.item.Avail}</span>
              </Link>
            </div>
          </div>
          <span className='card-content right' style={{ cursor: 'pointer' }}>
            <span
              style={{ fontSize: 20 }}
              // onClick={this.props.add}
              id={this.props.item.Name}
            >
              {this.props.item.Name}
            </span>
            <br />
            <span className='center'>Price : {this.props.item.Price}</span>
          </span>
        </span>
      </span>
    );
  }
}
export default ItemList;
