import React from "react";
import fetch from "./Fetches";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      products: [],
      isloading: "Loading...."
    };
    this.fetchData = this.fetchData.bind(this);
  }
  componentDidMount() {
    // console.log(this.state);
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    let items = [];
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await items.push(doc.data().Name);
    });
    await this.setState({ products: items, isloading: "Done" });
  };
  render() {
    return (
      <div>
        {this.state.isloading}
        {this.state.isloading === "Done" &&
          this.state.products.map(i => {
            <div>{i}</div>;
          })}
      </div>
    );
  }
}

export default App;
