import React from "react";
import { fetch } from "./Fetches";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      products: [],
      selected_items: [],
      items: [],
      isloading: "Loading...."
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push(doc.data());
    });
    await this.setState({ isloading: "Done" });
  };
  add = e => {
    console.log("Adding...", e.target.id);
    let a = e.target.id;
    if (!this.state.items.includes(a)) {
      this.setState(prev => {
        return {
          selected_items: [...prev.selected_items, { [a]: 1 }],
          items: [...prev.items, a]
        };
      });
    }
  };
  Billing = () => {
    console.log(this.state.selected_items);
  };
  render() {
    return (
      <div className="row">
        <div className="col s6 m8 l8">
          {console.log(this.state)}
          <h5>{this.state.isloading}</h5>
          {this.state.isloading === "Done" &&
            this.state.products.map(i => {
              return (
                <div onClick={this.add} id={i.Name} key={i.Name}>
                  {i.Name} {i.Avail} @{i.Price}
                </div>
              );
            })}
        </div>
        <div className="col s6 m4 l4">
          Selected Items
          {this.state.items &&
            this.state.items.map(i => {
              return (
                <div key={i}>
                  {i}
                  <input type="text" size="2" />
                </div>
              );
            })}
          <button onClick={this.Billing}>Bill it</button>
          {console.log(this.state.items.map(i => i))}
        </div>
      </div>
    );
  }
}

export default App;
