import React from 'react';
import { fetch } from '../../Fetches';
import Homepage from '../Layouts/Homepage';

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      selected_items: [],
      items: [],
      totPrice: 0,
      isloading: 'Loading...',
      list: []
    };
  }
  componentDidMount() {
    this.props.location.state &&
      this.setState({
        selected_items: this.props.location.state.selected_items,
        items: this.props.location.state.items,
        totPrice: this.props.location.state.totPrice
      });
    this.fetchData();
  }
  check = () => {
    this.fetchData();
  };
  fetchData = async () => {
    let a = await fetch();
    await console.log('fetch_over');
    await a.forEach(async doc => {
      await this.state.products.push({ ...doc.data(), id: doc.id });
    });
    (await this.state.products.length) !== 0 &&
      this.setState({ isloading: '' });
    (await this.setState({
      products: this.state.products.sort(b1.Avail - a1.Avail).reverse()
    })(await a.size)) === 0 && this.setState({ isloading: 'serverError' });
    (await this.state.isloading) === 'serverError' && this.check();
  };
  one = async e => {
    let name = e.target.name;
    let num = +e.target.id;
    if (
      this.state.selected_items[name].req + num >
      this.state.products.filter(i => i.Name === name).map(i => i.Avail)[0]
    ) {
      alert('Quantity Exceeded');
    } else if (!(this.state.selected_items[name].req + num < 0)) {
      if (this.state.selected_items[name].req + num === 0) {
        this.delete(e);
      }
      let a = Object.assign({}, this.state.selected_items, {
        ...this.state.selected_items,
        [name]: {
          ...this.state.selected_items[name],
          req: this.state.selected_items[name].req + num,
          totPrice:
            +this.state.products
              .filter(i => i.Name === name)
              .map(i => i.Price)[0] *
            (this.state.selected_items[name].req + num)
        }
      });
      await this.setState({ selected_items: a });
      await this.tot();
    }
  };
  add = async e => {
    let a = await e.target.id;
    let item = await this.state.products.filter(i => i.Name === a);
    if (a !== '' && item[0].Avail > 0) {
      if (!this.state.items.includes(a)) {
        this.setState(prev => {
          return {
            selected_items: {
              ...prev.selected_items,
              [a]: {
                req: 1,
                totavail: +item.map(i => i.Avail)[0],
                id: this.state.products
                  .filter(i => i.Name === a)
                  .map(i => i.id)[0],
                totPrice: +item.map(i => i.Price)[0],
                price: +item.map(i => i.Price)[0]
              }
            }
          };
        });
        await this.setState({
          items: [...this.state.items, a]
        });
        await this.tot();
        await this.setState({ list: [] });
      }
    } else alert('select valid product');
  };
  tot = () => {
    let a =
      this.state.items.length !== 0
        ? this.state.items
            .map(i => {
              return this.state.selected_items[i].totPrice;
            })
            .reduce(this.summation)
        : 0;
    this.setState({ totPrice: a });
  };
  delete = async e => {
    let name = e.target.id;
    let res = Object.assign({}, this.state.selected_items);
    delete res[name];
    await this.setState({ items: this.state.items.filter(i => i !== name) });
    await this.setState({ selected_items: res });
    await this.tot();
  };
  summation = (a, b) => a + b;
  search = e => {
    let list = [];
    this.state.products
      .filter(i => i.Name.toLowerCase().includes(e.target.value))
      .map(i => {
        return list.push(i);
      });
    e.target.value.length !== 0 && this.setState({ list: list });
    e.target.value.length === 0 && this.setState({ list: [] });
  };
  render() {
    return (
      <Homepage
        state={this.state}
        add={this.add}
        tot={this.tot}
        delete={this.delete}
        one={this.one}
        search={this.search}
      />
    );
  }
}

export default Dashboard;
