import firebase from 'firebase';

var firebaseConfig = {
  apiKey: 'AIzaSyAMXvz4AD4bx1NvJchOugf1uYvPhf8xBiA',
  authDomain: 'test-1528871171306.firebaseapp.com',
  databaseURL: 'https://test-1528871171306.firebaseio.com',
  projectId: 'test-1528871171306',
  storageBucket: 'test-1528871171306.appspot.com',
  messagingSenderId: '791882157170',
  appId: '1:791882157170:web:093a1485e8da828a'
};
// Initialize Firebase
firebase.initializeApp(firebaseConfig);
var firestore = firebase.firestore();
const docRef = firestore.collection('Products');

const fetch = async () => {
  return await docRef
    .get()
    .then(async querySnapshot => {
      return await querySnapshot;
    })
    .catch(err => {
      console.error('Error Occured', err);
      return err;
    });
};

const addnew = async (state, push) => {
  let a = 0;
  await docRef.get().then(async querySnapshot => {
    await querySnapshot.forEach(async doc => {
      a = await +doc.id;
    });
  });
  await ++a;
  let data = {
    Avail: state.Avail,
    DetailAvail_ltd: { [new Date().toDateString().slice(4)]: state.Avail },
    Name: state.Name,
    Price: state.Price,
    Wnt_ltd: state.wanted,
    pcs_per_Box: state.perBox
  };
  await docRef.doc(a.toString()).set(data);
  await push('/');
};
const remove = async (state, date) => {
  let b = await docRef
    .doc(state.id)
    .get()
    .then(async querySnapshot => {
      return await querySnapshot.data();
    });
  let q = await Object.assign({}, b, {
    ...b,
    Avail: b.Avail - b.DetailAvail_ltd[date]
  });
  delete q.DetailAvail_ltd[Object.keys(q.DetailAvail_ltd)];
  return await Object.assign({}, state, {
    ...state,
    num: 0,
    Price: await q.Price,
    DetailAvail_ltd: await q.DetailAvail_ltd,
    Avail: await q.Avail
  });
};
const fetchDetailAvail = async id => {
  return await docRef
    .doc(id)
    .get()
    .then(async querySnapshot => {
      return await querySnapshot.data();
    });
};

const add = async state => {
  let a = await docRef
    .doc(state.id)
    .get()
    .then(async querySnapshot => {
      return await querySnapshot.data().Avail;
    });
  let data = { [state.date]: state.num };
  let b = await docRef
    .doc(state.id)
    .get()
    .then(async querySnapshot => {
      return await querySnapshot.data();
    });
  let bDetailAvail_ltd = b.DetailAvail_ltd;
  let c = await { ...b.DetailAvail, ...data };
  if (Object.keys(bDetailAvail_ltd).length === 5) {
    let ele = Object.keys(bDetailAvail_ltd)[0];
    delete bDetailAvail_ltd[ele];
  }
  bDetailAvail_ltd = Object.assign({}, bDetailAvail_ltd, {
    ...bDetailAvail_ltd,
    ...data
  });
  await docRef.doc(state.id).update('DetailAvail', c);
  await docRef.doc(state.id).update('DetailAvail_ltd', bDetailAvail_ltd);
  await docRef.doc(state.id).update('Avail', a + +state.num);
  return await Object.assign({}, state, {
    Price: await docRef
      .doc(state.id)
      .get()
      .then(async querySnapshot => {
        return await querySnapshot.data().Price;
      }),
    num: 0,
    DetailAvail: await docRef
      .doc(state.id)
      .get()
      .then(async querySnapshot => {
        return await querySnapshot.data().DetailAvail;
      }),
    Avail: await docRef
      .doc(state.id)
      .get()
      .then(async querySnapshot => {
        return await querySnapshot.data().Avail;
      }),
    DetailAvail_ltd: await docRef
      .doc(state.id)
      .get()
      .then(async querySnapshot => {
        return await querySnapshot.data().DetailAvail_ltd;
      })
  });
};

const update = async (state, push) => {
  state.all.map(async i => {
    return await docRef
      .doc(state.item[i].id)
      .update('Avail', state.item[i].totavail - state.item[i].req)
      .then(push('/'));
  });
};

export { fetch, update, add, fetchDetailAvail, addnew, remove };
