import React from "react";
// import fetch from "./Fetches";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      products: [],
      isloading: "Loading...."
    };
    this.fetchData = this.fetchData.bind(this);
  }
  componentDidMount() {
    // console.log(this.state);
    this.fetchData();
  }
  fetchData = async () => {
    let a = await window.fetchfetch();
    let items = [];
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await items.push(doc.data().Name);
    });
    await this.setState({ products: items, isloading: "Done" });
  };
  render() {
    return (
      <div>
        {this.state.isloading}
        {console.log(this.state)}
      </div>
    );
  }
}

export default App;
