import React from 'react';
import ItemList from './ItemList';
import SelectedItemList from './Selected_ItemList';
import Footer from './Footer';
import Navbar from './Navbar';

const Homepage = props => {
  return (
    <div>
      {props.state.isloading === '' ? (
        <div>
          <div className='row'>
            <div>
              <Navbar empty={props.empty} search={props.search} />
            </div>
          </div>
          <div className='row' style={{ marginTop: '5%' }}>
            {props.state.list && (
              <div style={{ marginTop: 1 }}>
                {props.state.list.map(i => {
                  return (
                    <div
                      key={i.id}
                      style={{ cursor: 'pointer' }}
                      className='chip'
                      id={i.Name}
                      onClick={props.add}
                    >
                      {i.Name}
                      <i className='close material-icons'>close</i>
                    </div>
                  );
                })}
              </div>
            )}
            <div
              className='col s12 m6 l8'
              style={{ overflow: 'auto', height: 502 }}
            >
              <div className='row'>
                {props.state.products.map(i => {
                  return <ItemList item={i} key={i.Name} add={props.add} />;
                })}
              </div>
            </div>
            <div
              className='col s12 m6 l4 z-depth-5 '
              style={{
                overflow: 'auto',
                height: 500
              }}
            >
              {props.state.items &&
                props.state.items.map((i, index) => {
                  return (
                    <SelectedItemList
                      item={props.state.selected_items[i]}
                      name={i}
                      key={index}
                      delt={props.delete}
                      one={props.one}
                    />
                  );
                })}
              <div>
                <Footer
                  selected_items={props.state.selected_items}
                  bill={props.state.totPrice}
                  all={props.state.items}
                  empty={props.empty}
                />
              </div>
            </div>
          </div>
        </div>
      ) : (
        (props.state.isloading === 'Loading...' || 'serverError') && (
          <React.Fragment>
            <div style={{ margin: '0 auto', width: '50%' }}>
              <img
                src='https://cdn.dribbble.com/users/563824/screenshots/3633228/untitled-5.gif'
                alt='Loading...'
                style={{ margin: '0 auto', width: '99%' }}
              />
            </div>
          </React.Fragment>
        )
      )}
    </div>
  );
};

export default Homepage;
