import React from "react";
import { fetch } from "./Fetches";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      products: [],
      isloading: "Loading...."
    };
  }
  componentDidMount() {
    // console.log(this.state);
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push(doc.data());
    });
    await this.setState({ isloading: "Done" });
  };
  add = () => {
    console.log("Adding...");
  };
  render() {
    return (
      <div className="row" ></div>
    );
  }
}

export default App;
