import axios from 'axios';

export const addBill = state => {
  console.log(state);
  return axios
    .post('http://localhost:3001/bills', {
      [new Date().toDateString().slice(4)]: { name: state.name, req: state.req }
    })
    .then(resp => {
      if (resp.status === 200) {
        console.log(resp);
        return resp;
      } else {
        console.error('error');
      }
    });
};
