import React from 'react';
import { Link } from 'react-router-dom';

const selectText = () => {
  const input = document.getElementById('search');
  input.focus();
  input.select();
};
const emptylist = () => {
  const input = document.getElementById('search');
  input.innerHTML = '';
  props.empty(1);
};
const Navbar = props => {
  return (
    <nav className='center-align' style={{ width: '98.5%' }}>
      <div className='nav-wrapper center-align'>
        <div className='input-field col s11 m11 l11'>
          <input
            id='search'
            type='search'
            required
            placeholder='Inventory App'
            onChange={props.search}
            autoFocus
            onClick={selectText}
          />
          <label className='label-icon' htmlFor='search'>
            <i className='material-icons'>search</i>
          </label>
          <i className='material-icons' onClick={() => emptylist(props.empty)}>
            close
          </i>
        </div>
        <Link to='/add'>
          <i className='waves-effect waves-light red lighten-2 hoverable material-icons'>
            add
          </i>
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
