import React from 'react';
import { add, fetchDetailAvail } from '../../Fetches';
class EditPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      date: new Date().toDateString().slice(4),
      num: '',
      id: this.props.location.state && this.props.location.state.id,
      detailAvail: 'Loading....'
    };
    this.change = this.change.bind(this);
  }
  componentDidMount() {
    typeof this.state.id === 'undefined'
      ? this.props.history.push('/')
      : this.detailAvail(this.state);
  }
  detailAvail = async () => {
    this.setState({ detailAvail: await fetchDetailAvail(this.state) });
  };
  change(e) {
    let { name, value } = e.target;
    this.setState({ [name]: value });
  }
  submit = async () => {
    this.setState({ detailAvail: 'Adding...' });
    add(this.state, this.props.history);
  };
  remove = () => {
    this.setState({ detailAvail: 'Deleting...' });
    add(this.state, this.props.history);
  };
  render() {
    return (
      <div className='row center-align' style={{ marginTop: '10%' }}>
        <div className='col s3 m3 l3' />
        <div className='col s6 m6 l6'>
          <input
            type='number'
            className='input-field inline'
            name='num'
            value={this.state.num}
            onChange={this.change}
          />
          <button className='btn btn-small' onClick={this.submit}>
            Submit
          </button>
          <div>
            {this.state.detailAvail && this.state.detailAvail === 'Loading....'
              ? 'Loading......'
              : this.state.detailAvail === 'Adding...'
              ? 'Adding......'
              : this.state.detailAvail === 'Deleting...'
              ? 'Deleting......'
              : Object.keys(this.state.detailAvail.DetailAvail).map(
                  (i, index) => {
                    return (
                      <div key={index}>
                        {index === 0 && (
                          <div>
                            <h6>Avail : {this.state.detailAvail.Avail}</h6>
                            <h6>Price : {this.state.detailAvail.Price}</h6>
                          </div>
                        )}
                        <h5>
                          {i} : {this.state.detailAvail.DetailAvail[i]}
                          <i
                            className='material-icons red-text'
                            onClick={this.remove}
                            id={i}
                          >
                            delete_forever
                          </i>
                        </h5>
                      </div>
                    );
                  }
                )}
          </div>
        </div>
        <div className='col s3 m3 l3' />
      </div>
    );
  }
}

export default EditPage;
