import axios from 'axios';

export const addBill = state => {
  console.log(state);
  return axios.post('http://localhost:3001/posts').then(resp => {
    if (resp.status === 200) {
      console.log(resp);
      return resp;
    } else {
      console.error('error');
    }
  });
};
