import React from "react";
// import fetch from "./Fetches";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      products: 0
    };
    this.fetchData = this.fetchData.bind(this);
  }
  componentDidMount() {
    // console.log(this.state);
    this.fetchData("hi");
  }
  fetchData = async () => {
    let a = await fetch();
    let items = [];
    await console.log("fetch_over");
    a.forEach(async doc => {
      await items.push(doc.data().Name);
    });
    this.setState(prev => {
      return {
        products: prev.products + 1
      };
    });
  };
  render() {
    return <div>HI{console.log(this.state)}</div>;
  }
}

export default App;
