import axios from 'axios';

export const addBill = (items, selected_items) => {
  let data = {};
  for (let i = 0; i < items.length; i++) {
    data = {
      ...data,
      [items[i]]: selected_items[items[i]].req
    };
  }
  return axios
    .post('http://localhost:3001/bills', {
      [new Date().toDateString().slice(4)]: { data }
    })
    .then(resp => resp);
};

export const Bills = () => {
  return axios.get('http://localhost:3001/bills').then(req => req.data);
};

