import React from 'react';
import ItemList from './ItemList';
import SelectedItemList from './Selected_ItemList';
import Footer from './Footer';
import Navbar from './Navbar';

const search = e => {
  let list = [];
  props.state.products
    .filter(i => i.Name.toLowerCase().includes(e.target.value))
    .map(i => {
      return list.push(i);
    });
  console.log(list);
};
const Homepage = props => {
  return (
    <div>
      {props.state.isloading === '' ? (
        <div className='row'>
          <Navbar search={search} />
          <div className='col s12 m6 l9'>
            <div className='row'>
              {props.state.products.map(i => {
                return <ItemList item={i} key={i.Name} add={props.add} />;
              })}
            </div>
          </div>
          <div
            className='col s12 m6 l3 z-depth-5 '
            style={{
              overflow: 'auto',
              marginTop: '1%',
              minHeight: '100%',
              height: 520
            }}
          >
            <div>
              {props.state.items &&
                props.state.items.map((i, index) => {
                  return (
                    <SelectedItemList
                      item={props.state.selected_items[i]}
                      name={i}
                      change={props.change}
                      key={index}
                      delt={props.delete}
                      one={props.one}
                    />
                  );
                })}
            </div>
            <Footer
              selected_items={props.state.selected_items}
              bill={props.state.totPrice}
              all={props.state.items}
            />
          </div>
          <br />
        </div>
      ) : props.state.isloading === 'Loading...' ? (
        <React.Fragment>
          <div className='progress'>
            <div className='indeterminate' />
          </div>
          <h1 style={{ textAlign: 'center' }}>Loading....</h1>
        </React.Fragment>
      ) : (
        <h1 style={{ textAlign: 'center' }}>Server Error</h1>
      )}
    </div>
  );
};

export default Homepage;
