import React from "react";
import { fetch } from "./Fetches";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      products: [],
      selected_items: [],
      items: [],
      isloading: "Loading...."
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push(doc.data());
    });
    await this.setState({ isloading: "Done" });
  };
  add = e => {
    console.log("Adding...", e.target.id);
    let a = e.target.id;
    if (!this.state.items.includes(a)) {
      this.setState(prev => {
        return {
          selected_items: { ...prev.selected_items, [a]: 1 },
          items: [...prev.items, a]
        };
      });
    }
  };
  change = e => {
    this.setState({
      selected_items: {
        ...this.state.selected_items,
        [e.target.name]: +e.target.value
      }
    });
  };
  Billing = () => {
    console.log(this.state.selected_items);
  };
  render() {
    return (
      <div className="row">
        <div className="col s12 m6 l8">
          {console.log(this.state)}
          <h5>{this.state.isloading}</h5>
          {this.state.isloading === "Done" &&
            this.state.products.map(i => {
              return (
                <div onClick={this.add} id={i.Name} key={i.Name}>
                  {i.Name} {i.Avail} @{i.Price}
                </div>
              );
            })}
        </div>
        <div className="col s12 m6 l4">
          Selected Items
          {this.state.items &&
            this.state.items.map(i => {
              return (
                <div key={i}>
                  {i}
                  <div class="row">
                    <div class="input-field col s12">
                      <input
                        name={i}
                        type="number"
                        size="2"
                        value={this.state.selected_items[i]}
                        onChange={this.change}
                      />
                    </div>
                  </div>
                </div>
              );
            })}
          {this.state.selected_items.length === 0 ? null : (
            <button onClick={this.Billing}>Bill it</button>
          )}
        </div>
      </div>
    );
  }
}

export default App;
