import React, { Component } from 'react';
import { addnew } from '../../Fetches';

class AddPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Name: 'Q',
      Avail: 1,
      Price: 1
    };
  }
  submit = () => {
    this.state.Name !== '' &&
      this.state.Avail !== '' &&
      this.state.Price !== '' &&
      addnew(this.state);
  };
  change = e => {
    let { name, value } = e.target;
    this.setState({ [name]: value });
  };
  render() {
    return (
      <div className='center-align'>
        <div class='row'>
          <div class='col s12 m7 l12'>
            Name :
            <div class='input-field inline'>
              <input
                type='text'
                name='Name'
                value={this.state.Name}
                onChange={this.change}
              />
              <label>Name</label>
            </div>
          </div>
        </div>
        <div className='row'>
          <div className='input-field col s12 m7 l7'>
            <input
              type='number'
              name='Avail'
              value={this.state.Avail}
              onChange={this.change}
            />
            <label>Stock</label>
          </div>
          <div className='input-field col s12 m7 l7'>
            <input
              type='number'
              name='Price'
              value={this.state.Price}
              onChange={this.change}
            />
            <label>Price per one</label>
          </div>
        </div>
        <button
          className='btn waves-effect waves-light red lighten-2'
          type='submit'
          name='action'
          onClick={this.submit}
        >
          Submit
          <i className='material-icons left'>send</i>
        </button>
      </div>
    );
  }
}

export default AddPage;
