import React from 'react';
import { Link } from 'react-router-dom';

class ItemList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      display: false
    };
  }
  show() {
    this.setState({ display: !this.state.display });
  }
  render() {
    return (
      <div className='col s12 m6 l4 '>
        <div
          className={
            this.props.item.Avail >= 10
              ? 'card hoverable waves-effect waves-block waves-red'
              : this.props.item.Avail === 0
              ? 'card waves-effect waves-block waves-grey grey lighten-1'
              : 'card hoverable waves-effect waves-block waves-red red lighten-4'
          }
        >
          <Link to={{ pathname: '/edit', state: this.props.item }}>
            <span className='right' style={{ marginRight: 10, fontSize: 20 }}>
              {this.props.item.Avail}
            </span>
          </Link>
          <div
            className='card-content'
            style={{ cursor: 'pointer' }}
            onClick={this.props.add}
            id={this.props.item.Name}
          >
            <span
              className='card-title truncate black-text darken-4'
              onClick={this.props.add}
              id={this.props.item.Name}
            >
              {this.props.item.Name}
            </span>
            <span
              className='card'
              onMouseOver={this.show}
              style={{ display: this.state.display ? 'block' : 'none' }}
            >
              {this.props.item.Name}
            </span>
            <div
              className='right'
              onClick={this.props.add}
              id={this.props.item.Name}
            >
              Price : {this.props.item.Price}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default ItemList;
