import React from "react";
import { fetch } from '../Components/'

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      selected_items: [],
      totPrice: 0,
      isloading: "Loading..."
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push(doc.data());
    });
    this.state.products && this.setState({ isloading: "Done" });
  };
  add = e => {
    console.log("Adding...", e.target.id);
    let a = e.target.id;
    if (!this.state.items.includes(a)) {
      this.setState(prev => {
        return {
          selected_items: [
            ...prev.selected_items,
            { name: a, req: 1, totPrice: 0 }
          ]
        };
      });
    }
  };
  render() {
    return (
      <div>
        <h5>{this.state.isloading}</h5>
        {this.state.isloading === "Done" &&
          this.state.products.map(i => {
            return (
              <div onClick={this.add} id={i.Name} key={i.Name}>
                {i.Name} {i.Avail} @ {i.Price}
              </div>
            );
          })}
      </div>
    );
  }
}

export default Dashboard;
