import React from "react";
import { fetch } from "../Fetches";
import ItemList from "./ItemList";

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      selected_items: [],
      totPrice: 0,
      items: [],
      isloading: "Loading..."
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push({ ...doc.data(), id: doc.id });
    });
    this.state.products && this.setState({ isloading: "" });
  };
  //   change = async e => {
  //     let id = e.target.id;
