  //       totPrice:
  //         +this.state.products.filter(i => i.id === id).map(i => i.Price)[0] *
  //         a.req,
  //       req: a.req
  //     });
  //     this.setState(prev => {
  //       return {
  //         selected_items: [...prev.selected_items, a1]
  //       };
  //     });
  //   };
  add = e => {
    console.log("Adding...", e.target.id);
    let a = e.target.id;
    if (!this.state.selected_items.map(i => i.id).includes(a)) {
      this.setState(prev => {
        return {
          selected_items: {
            ...prev.selected_items,
            [this.state.products.filter(i => i.id === a).map(i => i.Name)[0]]: {
              req: 1,
              totPrice: +this.state.products
                .filter(i => i.id === a)
                .map(i => i.Price)[0]
            }
          }
        };
      });
      this.setState({
        items: [
          ...this.state.items,
          this.state.products.filter(i => i.id === a).map(i => i.Name)[0]
        ]
      });
    }
  };
  //   summation = (a, b) => a + b;
  render() {
    return (
      <div className="row">
        <div className="col s12 m6 l6">
          <h5>{this.state.isloading}</h5>
          {console.log(this.state.items)}
          {this.state.isloading !== "Loading..." &&
            this.state.products.map(i => {
              return <ItemList item={i} key={i.Name} add={this.add} />;
            })}
        </div>
        <div
          className="col s12 m6 l6"
          style={{
            overflow: "auto",
            backgroundColor: "rgb(213, 237, 218)",
            minHeight: "100%",
            height: 520
          }}
        >
          <table>
            <tbody>
              {this.state.items &&
                this.state.items.map(i =>
                  console.log(this.state.selected_items[i].req)
                )}
            </tbody>
          </table>
          {/* <div className="footer-copyright">
            <div className="container" style={{ position: "fixed", bottom: 0 }}>
              {this.state.selected_items.length !== 0 ? (
                <div className="row">
                  <div className="col s12 m6 l6">
                    <button className="waves-effect waves-light btn">
                      Bill it!
                    </button>
                  </div>
                  <div
                    className="col s12 m6 l6"
                    style={{ width: "50%", marginLeft: -10 }}
                  >
                    <h5>
                      Grand Total :
                      {this.state.items
                        .map(i => {
                          return this.state.selected_items[i].totPrice;
                        })
                        .reduce(this.summation)}
                    </h5>
                  </div>
                </div>
              ) : null}
            </div>
          </div> */}
          <br />
        </div>
      </div>
    );
  }
}

export default Dashboard;
