import React from "react";
import { fetch } from "../Fetches";
import ItemList from "./ItemList";
import SelectedItemlist from "./Selected_ItemList";

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      selected_items: [],
      totPrice: 0,
      isloading: "Loading..."
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push({ ...doc.data(), id: doc.id });
    });
    this.state.products && this.setState({ isloading: "" });
  };
  change = async e => {
    let id = e.target.id;
    let item = this.state.selected_items.filter(i => i.id === id)[0];
    let a = await Object.assign({}, item, { req: item.req + 1 });
    let a1 = await Object.assign({}, item, {
      totPrice:
        +this.state.products.filter(i => i.id === id).map(i => i.Price)[0] *
        a.req
    });
    this.setState({
      selected_items: [a1]
    });
  };
  add = e => {
    console.log("Adding...", e.target.id);
    let a = e.target.id;
    // if (!this.state.selected_items.map(i => i.id).includes(a)) {
    this.setState(prev => {
      return {
        selected_items: [
          ...prev.selected_items,
          {
            id: a,
            name: this.state.products
              .filter(i => i.id === a)
              .map(i => i.Name)[0],
            req: 1,
            totPrice: +this.state.products
              .filter(i => i.id === a)
              .map(i => i.Price)[0]
          }
        ]
      };
    });
    // }
  };
  summation = (a, b) => a + b;
  render() {
    return (
      <div className="row">
        <div className="col s12 m6 l6">
          <h5>{this.state.isloading}</h5>
          {console.log(this.state.selected_items)}
          {this.state.isloading !== "Loading..." &&
            this.state.products.map(i => {
              return <ItemList item={i} key={i.Name} add={this.add} />;
            })}
        </div>
        <div
          className="col s12 m6 l6"
          style={{
            backgroundColor: "grey",
            overflow: "auto",
            minHeight: "100%",
            height: 110
          }}
        >
          <table>
            <tbody>
              {this.state.selected_items &&
                this.state.selected_items.map(i => {
                  return (
                    <SelectedItemlist
                      item={i}
                      key={i.id}
                      change={this.change}
                    />
                  );
                })}
            </tbody>
          </table>
          <div>
            {this.state.selected_items.length !== 0 ? (
              <div>
                <button>Bill</button>
                {this.state.selected_items
                  .map(i => i.totPrice)
                  .reduce(this.summation)}
              </div>
            ) : null}
          </div>
          <br />
        </div>
      </div>
    );
  }
}

export default Dashboard;
