import React from "react";
import { fetch } from "../Fetches";
import ItemList from "./ItemList";
import SelectedItemList from "./Selected_ItemList";

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      selected_items: [],
      totPrice: 0,
      items: [],
      isloading: "Loading..."
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push({ ...doc.data(), id: doc.id });
    });
    this.state.products && this.setState({ isloading: "" });
  };
  change = async e => {
    let name = e.target.id;
    console.log(
      this.state.products.filter(i => i.Name === name).map(i => i.Price)[0]
    );
    let a = Object.assign({}, this.state.selected_items, {
      ...this.state.selected_items,
      [name]: {
        req: this.state.selected_items[name].req + 1
      }
    });
    this.setState({ selected_items: a });
  };
  add = e => {
    let a = e.target.id;
    console.log("Added", e.target.id);
    if (!this.state.items.includes(a)) {
      this.setState(prev => {
        return {
          selected_items: {
            ...prev.selected_items,
            [this.state.products
              .filter(i => i.Name === a)
              .map(i => i.Name)[0]]: {
              req: 1,
              totPrice: +this.state.products
                .filter(i => i.Name === a)
                .map(i => i.Price)[0]
            }
          }
        };
      });
      this.setState({
        items: [...this.state.items, a]
      });
    }
  };
  //   summation = (a, b) => a + b;
  render() {
    return (
      <div className="row">
        <div className="col s12 m6 l6">
          <h5>{this.state.isloading}</h5>
          {this.state.items.length !== 0 && console.log(this.state.items)}
          {this.state.isloading !== "Loading..." &&
            this.state.products.map(i => {
              return <ItemList item={i} key={i.Name} add={this.add} />;
            })}
        </div>
        <div
          className="col s12 m6 l6"
          style={{
            overflow: "auto",
            backgroundColor: "rgb(213, 237, 218)",
            minHeight: "100%",
            height: 520
          }}
        >
          <table>
            <tbody>
              {this.state.items &&
                this.state.items.map((i, index) => {
                  return (
                    <SelectedItemList
                      item={this.state.selected_items[i]}
                      name={i}
                      change={this.change}
                      key={index}
                    />
                  );
                })}
            </tbody>
          </table>
          {/* <div className="footer-copyright">
            <div className="container" style={{ position: "fixed", bottom: 0 }}>
              {this.state.selected_items.length !== 0 ? (
                <div className="row">
                  <div className="col s12 m6 l6">
                    <button className="waves-effect waves-light btn">
                      Bill it!
                    </button>
                  </div>
                  <div
                    className="col s12 m6 l6"
                    style={{ width: "50%", marginLeft: -10 }}
                  >
                    <h5>
                      Grand Total :
                      {this.state.items
                        .map(i => {
                          return this.state.selected_items[i].totPrice;
                        })
                        .reduce(this.summation)}
                    </h5>
                  </div>
                </div>
              ) : null}
            </div>
          </div> */}
          <br />
        </div>
      </div>
    );
  }
}

export default Dashboard;
