import React from 'react';
import ItemList from './ItemList';
import SelectedItemList from './Selected_ItemList';
import Footer from './Footer';
import Navbar from './Navbar';
import loading from '../../Gif/Loading/loading.gif';
import Chip from './Chip';
import { Link } from 'react-router-dom';

const Homepage = props => {
  return (
    <div>
      <div className='row'>
        <div
          className='col s12 m6 l8'
          style={{
            overflow: 'auto',
            height: window.innerWidth < 500 ? 450 : 560,
            marginBottom: window.innerWidth < 500 && '40%'
          }}
        >
          <div className='row'>
            <Navbar empty={props.empty} search={props.search} />
            <ul id='slide-out' style={{ position: 'sticky' }}>
              <li>
                <Link to='/add'>Add Product</Link>
              </li>
              <li>
                <div className='divider' />
              </li>
              <li>
                <Link to='/'>Reports</Link>
              </li>
              <li>
                <Link to='/'>Wanted List</Link>
              </li>
            </ul>
            <Chip list={props.state.list} add={props.add} />
            {props.state.isloading === '' ? (
              props.state.products.map(i => {
                return <ItemList item={i} key={i.Name} add={props.add} />;
              })
            ) : props.state.isloading === 'Loading...' ? (
              <React.Fragment>
                <div style={{ margin: '0 auto', width: '50%' }}>
                  <img
                    src={loading}
                    alt='Loading...'
                    style={{ margin: '0 auto', width: '99%' }}
                  />
                </div>
              </React.Fragment>
            ) : (
              <div
                style={{
                  marginLeft: window.innerWidth < 500 ? '30%' : '40%',
                  marginTop: '20%'
                }}
              >
                <b style={{ fontSize: 30 }}>ServerError</b>
                <div style={{ marginLeft: 20 }}>Refresh the page</div>
              </div>
            )}
          </div>
        </div>
        <div
          className='col s12 m6 l4 z-depth-5 '
          style={{
            overflow: 'auto',
            height: 560,
            marginTop: window.innerWidth < 500 ? '-40%' : 5,
            marginLeft: window.innerWidth > 500 && -5
          }}
        >
          <Footer
            selected_items={props.state.selected_items}
            bill={props.state.totPrice}
            all={props.state.items}
            empty={props.empty}
          />
          <div style={{ overflow: 'auto', height: 400 }}>
            {props.state.items.length !== 0 ? (
              props.state.items.map((i, index) => {
                return (
                  <SelectedItemList
                    item={props.state.selected_items[i]}
                    name={i}
                    key={index}
                    delt={props.delete}
                    one={props.one}
                  />
                );
              })
            ) : (
              <h5 className='center'>--NONE--</h5>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Homepage;
