import React from 'react';
import ItemList from './ItemList';
import SelectedItemList from './Selected_ItemList';
import Footer from './Footer';
import Navbar from './Navbar';

const Homepage = props => {
  return (
    <div>
      {props.state.isloading === '' ? (
        <div className='row'>
          <Navbar search={props.search} />
          {props.state.list && (
            <div style={{ marginTop: 10 }}>
              {props.state.list.map(i => {
                return (
                  <div>
                    <div
                      style={{ cursor: 'pointer' }}
                      className='chip tooltipped'
                      data-position='bottom'
                      data-tooltip={`Avail : ${i.Avail}`}
                      key={i.id}
                      id={i.Name}
                      onClick={props.add}
                    >
                      {i.Name}
                      <i className='close material-icons'>close</i>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
          <div className='col s12 m6 l8'>
            <div className='row'>
              {props.state.products.map(i => {
                return <ItemList item={i} key={i.Name} add={props.add} />;
              })}
            </div>
          </div>
          <div
            className='col s12 m6 l4 z-depth-5 '
            style={{
              overflow: 'auto',
              height: 400
            }}
          >
            {props.state.items &&
              props.state.items.map((i, index) => {
                return (
                  <SelectedItemList
                    item={props.state.selected_items[i]}
                    name={i}
                    change={props.change}
                    key={index}
                    delt={props.delete}
                    one={props.one}
                  />
                );
              })}
            <Footer
              selected_items={props.state.selected_items}
              bill={props.state.totPrice}
              all={props.state.items}
            />
          </div>
          <br />
        </div>
      ) : props.state.isloading === 'Loading...' ? (
        <React.Fragment>
          <div className='progress'>
            <div className='indeterminate' />
          </div>
          <h1 style={{ textAlign: 'center' }}>Loading....</h1>
        </React.Fragment>
      ) : (
        <h1 style={{ textAlign: 'center' }}>Server Error</h1>
      )}
    </div>
  );
};

export default Homepage;
