import firebase from 'firebase';

var firebaseConfig = {
  apiKey: 'AIzaSyAMXvz4AD4bx1NvJchOugf1uYvPhf8xBiA',
  authDomain: 'test-1528871171306.firebaseapp.com',
  databaseURL: 'https://test-1528871171306.firebaseio.com',
  projectId: 'test-1528871171306',
  storageBucket: 'test-1528871171306.appspot.com',
  messagingSenderId: '791882157170',
  appId: '1:791882157170:web:093a1485e8da828a'
};
// Initialize Firebase
firebase.initializeApp(firebaseConfig);
var firestore = firebase.firestore();
const docRef = firestore.collection('Products');

const fetch = async () => {
  await console.log('in fetch');
  return await docRef
    .get()
    .then(async querySnapshot => {
      return await querySnapshot;
    })
    .catch(err => {
      console.error('Error Occured', err);
      return err;
    });
};

const addnew = async (state, push) => {
  let a = 0;
  await docRef.get().then(async querySnapshot => {
    await querySnapshot.forEach(async doc => {
      a = await +doc.id;
    });
  });
  await ++a;
  let data = {
    Avail: state.Avail,
    DetailAvail: { [new Date().toDateString().slice(4)]: state.Avail },
    Name: state.Name,
    Price: state.Price
  };
  await docRef.doc(a.toString()).set(data);
  await push('/');
};
const remove = (id, push, date) => {};
const fetchDetailAvail = async state => {
  return await docRef
    .doc(state.id)
    .get()
    .then(async querySnapshot => {
      return await querySnapshot.data();
    });
};

const add = async (state, history) => {
  let a = await docRef
    .doc(state.id)
    .get()
    .then(async querySnapshot => {
      return await querySnapshot.data().Avail;
    });
  let data = { [state.date]: state.num };
  let b = await docRef
    .doc(state.id)
    .get()
    .then(async querySnapshot => {
      return await querySnapshot.data().DetailAvail;
    });
  let c = await { ...b, ...data };
  await docRef
    .doc(state.id)
    .update('DetailAvail', c)
    .then(() => console.log('added'));
  await docRef
    .doc(state.id)
    .update('Avail', a + +state.num)
    .then(() => console.log('added'));
  await history.push('/');
};

const update = async (state, history) => {
  state.all.map(async i => {
    return await docRef
      .doc(state.item[i].id)
      .update('Avail', state.item[i].totavail - state.item[i].req);
  });
  await history.push('/');
};

export { fetch, update, add, fetchDetailAvail, addnew, remove };
