import React from 'react';
import { update } from '../../Fetches/Fetches';
import logo from '../../Gif/logo.png';

class Billpage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      amt: 0,
      show: false
    };
  }
  show = () => {
    this.setState({ show: !this.state.show });
  };
  change = e => {
    this.setState({ amt: +e.target.value });
  };
  purchase = async () => {
    // update(this.props.location.state, this.props.history.push);
    window.print();
  };
  Cancelpurchase = () => {
    this.props.history.push({
      pathname: '/',
      state: {
        selected_items: this.props.location.state.item,
        items: this.props.location.state.all,
        totPrice: this.props.location.state.bill
      }
    });
  };
  componentDidMount() {
    console.log(JSON.parse(localStorage.getItem('localStore')));
    return this.props.location.state ? null : this.props.history.push('/');
  }
  render() {
    return this.props.location.state ? (
      <div className='row'>
        <div
          className={this.state.show ? 'col s12 m7 l7' : null}
          style={{ width: this.state.show ? null : '90%', margin: 'auto' }}
        >
          <div>
            <img
              src={logo}
              className='left'
              style={{ marginTop: 0 }}
              alt='SL Ice&Store'
            />
            <h5>Seethalakshmi Ice&Store</h5>
            <b className='right'>Date : {new Date().toDateString()}</b>
            <br />
            <div className='card'>
              <table className='centered striped'>
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Item Qty</th>
                    <th>Item Price</th>
                  </tr>
                </thead>
                <tbody>
                  {this.props.location.state.all.map((i, index) => {
                    return (
                      <tr key={index}>
                        <td>{i}</td>
                        <td>{this.props.location.state.item[i].req}</td>
                        <td>
                          Rs. {this.props.location.state.item[i].totPrice}
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
          <br />
          <h4 className='right'>Total {this.props.location.state.bill}</h4>
          <button
            className='waves-effect waves-light btn'
            onClick={() => this.props.history.push('/')}
          >
            <i className='material-icons left'>cancel</i>
            Cancel
          </button>
          &nbsp;&nbsp;
          <button
            className='waves-effect waves-light btn'
            onClick={this.Cancelpurchase}
          >
            <i className='material-icons left'>add_shopping_cart</i>
            Add Products
          </button>
          &nbsp;&nbsp;
          <button
            className='waves-effect waves-light btn'
            onClick={this.purchase}
          >
            <i className='material-icons right'>send</i>
            Make Purchase
          </button>
        </div>
        {this.state.show && (
          <div className='col s11 m4 l4 offset-s1 m1 l1'>
            products Price:
            <input
              type='number'
              disabled
              value={this.props.location.state.bill}
            />
            Given Price:
            <input
              type='text'
              autoFocus
              value={this.state.amt}
              onChange={this.change}
            />
            Remaining :
            <input
              type='number'
              disabled
              value={this.state.amt - this.props.location.state.bill}
            />
          </div>
        )}
        <button
          className='btn-floating btn-large waves-effect waves-light red material-icons hoverable'
          style={{ position: 'absolute', bottom: 8, right: 16 }}
          onClick={this.show}
        >
          computer
        </button>
      </div>
    ) : (
      <div>Click here to move to home page</div>
    );
  }
}
export default Billpage;
