import React from 'react';
import { add, fetchDetailAvail, remove } from '../../Fetches';
import Alert from '../Layouts/Modals/alert';
class EditPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      date: new Date().toDateString().slice(4),
      num: '',
      id: this.props.location.state && this.props.location.state.id,
      detailAvail: 'Loading....'
    };
  }
  componentDidMount() {
    typeof this.state.id === 'undefined'
      ? this.props.history.push('/')
      : this.detailAvail(this.state);
  }
  detailAvail = async () => {
    this.setState({ detailAvail: await fetchDetailAvail(this.state) });
  };
  change = async e => {
    let { name, value } = e.target;
    this.setState({ [name]: value });
  };
  submit = async () => {
    await this.setState({ detailAvail: 'Adding...' });
    let a = await add(this.state);
    await <Alert text={'Added....'} />;
    await this.setState({
      detailAvail: await a
    });
  };
  remove = async e => {
    let { id } = e.target;
    let a = window.confirm('R U Sure...?');
    a && (await this.setState({ detailAvail: 'Deleting...' }));
    a && (await alert('Deleted....'));
    a &&
      (await this.setState({
        detailAvail: await remove(this.state, id)
      }));
  };
  render() {
    return (
      <div className='row center-align' style={{ marginTop: '10%' }}>
        <div className='col s3 m3 l3' />
        <div className='col s6 m6 l6'>
          <input
            type='number'
            className='input-field inline'
            name='num'
            value={this.state.num}
            onChange={this.change}
          />
          <button className='btn btn-small' onClick={this.submit}>
            Submit
          </button>
          &nbsp;&nbsp;
          <button
            className='btn btn-small'
            onClick={() => this.props.history.goBack()}
          >
            Cancel
          </button>
          <div>
            {this.state.detailAvail && this.state.detailAvail === 'Loading....'
              ? 'Loading...'
              : this.state.detailAvail === 'Adding...'
              ? 'Adding......'
              : this.state.detailAvail === 'Deleting...'
              ? 'Deleting......'
              : Object.keys(this.state.detailAvail.DetailAvail).map(
                  (i, index) => {
                    return (
                      <div key={index}>
                        {index === 0 && (
                          <h6>
                            Avail : {this.state.detailAvail.Avail}
                            &nbsp;&nbsp; Price : {this.state.detailAvail.Price}
                          </h6>
                        )}
                        <h5>
                          {i} : {this.state.detailAvail.DetailAvail[i]}
                          &nbsp;
                          <i
                            className='material-icons red-text'
                            onClick={this.remove}
                            id={i}
                            style={{ cursor: 'pointer' }}
                          >
                            delete_forever
                          </i>
                        </h5>
                      </div>
                    );
                  }
                )}
          </div>
        </div>
        <div className='col s3 m3 l3' />
      </div>
    );
  }
}

export default EditPage;
