import React from "react";
import fetch from "./Fetches";

class App extends React.Component {
  constructor() {
    super();
    this.setState = {
      data: null
    };
  }
  componentDidMount() {
    console.log(this.state);  
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    a.forEach(async doc => {
      await this.setState({ data: doc.data().Name });
    });
  };
  render() {
    return <div>HI{this.state && console.log(this.state)}</div>;
  }
}

export default App;
