import React from 'react';
import { add, fetchDetailAvail } from '../../Fetches';
class EditPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      date: new Date().toDateString().slice(4),
      num: '',
      id: this.props.location.state && this.props.location.state.id,
      detailAvail: ''
    };
    this.change = this.change.bind(this);
  }
  componentDidMount() {
    typeof this.state.id === 'undefined' && this.props.history.push('/');
    this.detailAvail();
  }
  detailAvail = async () => {
    this.setState({ detailAvail: await fetchDetailAvail(this.state) });
  };
  change(e) {
    let { name, value } = e.target;
    this.setState({ [name]: value });
  }
  submit = async () => {
    add(this.state, this.props.history);
  };
  render() {
    return (
      <div className='center-align'>
        {console.log(this.state)}
        <div className='col 3' />
        <div className='col 6'>
          <input
            type='number'
            className='input-field'
            name='num'
            value={this.state.num}
            onChange={this.change}
          />
          <button className='btn btn-small' onClick={this.submit}>
            Submit
          </button>
          <div>Detail Avail.....</div>
        </div>
        <div className='col 3' />
      </div>
    );
  }
}

export default EditPage;
