import React from 'react';
import { add, fetchDetailAvail, remove } from '../../Fetches/Fetches';
import DeletingGif from '../Layouts/DeletingGif';

class EditPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      date: new Date().toDateString().slice(4),
      num: '',
      id: this.props.match.params.id,
      detailAvail: 'Loading....',
      wanted: '',
      perBox: ''
    };
  }
  componentDidMount() {
    this.detailAvail();
  }
  detailAvail = async () => {
    await this.setState({ detailAvail: await fetchDetailAvail(this.state) });
    await this.setState({ wanted: await this.state.detailAvail.Wnt_ltd });
    await this.setState({ perBox: await this.state.detailAvail.Pcs_per_Box });
  };
  change = async e => {
    let { name, value } = e.target;
    this.setState({ [name]: value });
  };
  submit = async () => {
    if (this.state.num !== '') {
      let a1 = window.confirm('R U Sure.....');
      a1 && (await this.setState({ detailAvail: 'Adding...' }));
      a1 &&
        (await this.setState({
          detailAvail: await add(this.state)
        }));
    } else {
      alert('Enter Something.....');
    }
  };
  remove = async e => {
    let { id } = e.target;
    let a = window.confirm('R U Sure...?');
    a && (await this.setState({ detailAvail: 'Deleting...' }));
    a &&
      (await this.setState({
        detailAvail: await remove(this.state, id)
      }));
  };
  render() {
    return (
      <div>
        <nav className='navbar-fixed'>
          <div className='nav-wrapper'>
            <div className='brand-logo center'>
              {this.state.detailAvail && this.state.detailAvail.Name}
            </div>
          </div>
        </nav>
        <div className='row center-align'>
          <div className='col s12 m12 l6'>
            {this.state.detailAvail &&
            this.state.detailAvail === 'Loading....' ? (
              <DeletingGif text={this.state.detailAvail} />
            ) : this.state.detailAvail === 'Adding...' ? (
              <DeletingGif text={this.state.detailAvail} />
            ) : this.state.detailAvail === 'Deleting...' ? (
              <DeletingGif text={this.state.detailAvail} />
            ) : (
              <table className='highlight centered'>
                {console.log(this.state)}
                <tbody>
                  {Object.keys(this.state.detailAvail.DetailAvail_ltd).map(
                    (i, index) => {
                      return (
                        <React.Fragment key={index}>
                          {index === 0 && (
                            <React.Fragment>
                              <tr>
                                <td>Avail : {this.state.detailAvail.Avail}</td>
                                <td>Price : {this.state.detailAvail.Price}</td>
                              </tr>
                              <tr>
                                <th>Purchased Date</th>
                                <th>Purchased Qty</th>
                                <th />
                              </tr>
                            </React.Fragment>
                          )}
                          <tr>
                            <td>{i}</td>
                            <td>{this.state.detailAvail.DetailAvail_ltd[i]}</td>
                            <td>
                              <i
                                className='material-icons red-text'
                                onClick={this.remove}
                                id={i}
                                style={{ cursor: 'pointer' }}
                              >
                                delete_forever
                              </i>
                            </td>
                          </tr>
                        </React.Fragment>
                      );
                    }
                  )}
                </tbody>
              </table>
            )}
          </div>
          <div className='col s12 m12 l6'>
            <div className='input-field inline'>
              <input
                type='number'
                name='num'
                value={this.state.num}
                onChange={this.change}
              />
              <label>New Purchase</label>
            </div>
            <br />
            <div className='input-field inline'>
              <input
                type='number'
                name='wanted'
                placeholder='Loading....'
                value={this.state.wanted}
                onChange={this.change}
              />
              <label>Wanted Limit</label>
            </div>
            <br />
            <div className='input-field inline'>
              <input
                type='number'
                name='perBox'
                placeholder='Loading....'
                value={this.state.perBox}
                onChange={this.change}
              />
              <label>Pieces per Box</label>
            </div>
            <br />
            <button className='btn btn-small' onClick={this.submit}>
              Submit
            </button>
            <button
              className='btn btn-small'
              onClick={() => this.props.history.push('/')}
            >
              Cancel
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default EditPage;
