import React from "react";
import { fetch } from "../Fetches";

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      selected_items: [],
      totPrice: 0,
      isloading: "Loading..."
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    let a = await fetch();
    await console.log("fetch_over");
    await a.forEach(async doc => {
      await this.state.products.push(doc.data());
    });
    this.state.products && this.setState({ isloading: "Done" });
  };
  add = e => {
    console.log("Adding...", e.target.id);
    let a = e.target.id;
    if (!this.state.selected_items.map(i => i.name).includes(a)) {
      this.setState(prev => {
        return {
          selected_items: [
            ...prev.selected_items,
            {
              name: a,
              req: 1,
              totPrice: +this.state.products
                .filter(i => i.Name === a)
                .map(i => i.Price)[0]
            }
          ]
        };
      });
    }
  };
  render() {
    return (
      <div className="row">
        <div className="col s12 m6 l6">
          <h5>{this.state.isloading}</h5>
          {console.log(this.state.selected_items)}
          {this.state.isloading === "Done" &&
            this.state.products.map(i => {
              return (
                <div onClick={this.add} id={i.Name} key={i.Name}>
                  {i.Name} {i.Avail} @ {i.Price}
                </div>
              );
            })}
        </div>
        <div className="col s12 m6 l6">
          <table>
            <th>Selected_items</th>
            {this.state.selected_items &&
              this.state.selected_items.map(i => {
                return (
                  <tr>
                    {i.name}
                    <td>
                      <label for="Req">
                        Req
                        <input className="col s4" type="number" id="Req" />
                      </label>
                      {/* &nbsp;&nbsp;&nbsp;
                      <input className="col s4 offset-s2" type="number" /> */}
                    </td>
                  </tr>
                );
              })}
          </table>
        </div>
      </div>
    );
  }
}

export default Dashboard;
