import React, { Component } from 'react';
import { Bills } from '../../Fetches/Queries';

class Reports extends Component {
  constructor() {
    super();
    this.state = {
      bill: '',
      keys: '',
      selected: ''
    };
    this.fetchBill = this.fetchBill.bind(this);
    this.select = this.select.bind(this);
  }
  fetchBill = async () => {
    let a = await Bills();
    await this.setState({ bill: a, keys: a.map(i => Object.keys(i)[0]) });
  };
  componentDidMount() {
    this.fetchBill();
  }
  render() {
    return (
      <div>
        {this.state.keys && (
          <ul>
            {this.state.keys.map((i, index) => (
              <li key={index} onClick={this.select}>
                {i}
              </li>
            ))}
          </ul>
        )}
        {console.log(this.state.selected)}
      </div>
    );
  }
}

export default Reports;
