import React from 'react';
import { update } from '../../Fetches/Fetches';

class Billpage extends React.Component {
  constructor() {
    super();
    this.state = {
      amt: 0
    };
  }
  change = e => {
    this.setState({ amt: +e.target.value });
  };
  purchase = () => {
    update(this.props.location.state, this.props.history);
  };
  Cancelpurchase = () => {
    this.props.history.push({
      pathname: '/',
      state: {
        selected_items: this.props.location.state.item,
        items: this.props.location.state.all,
        totPrice: this.props.location.state.bill
      }
    });
  };
  componentDidMount() {
    return this.props.location.state ? null : this.props.history.push('/');
  }
  render() {
    return this.props.location.state ? (
      <div className='row'>
        <div className='col s12 m7 l7'>
          <div style={{ backgroundColor: 'grey' }}>
            {this.props.location.state.all.map((i, index) => {
              return (
                <div key={index}>
                  {i} => {this.props.location.state.item[i].req}
                  Rs. {this.props.location.state.item[i].totPrice}
                </div>
              );
            })}
            @ {new Date().toDateString()}
            <br />
            Total {this.props.location.state.bill}
          </div>
          <br />
          <button onClick={this.purchase}>Continue</button>&nbsp;&nbsp;
          <button onClick={this.Cancelpurchase}>Cancel</button>
        </div>
        <div className='col s11 m4 l4 offset-s1 m1 l1'>
          products Price:
          <input
            type='number'
            disabled
            value={this.props.location.state.bill}
          />
          Given Price:
          <input
            type='text'
            autoFocus
            value={this.state.amt}
            onChange={this.change}
          />
          Remaining :
          <input
            type='number'
            disabled
            value={this.state.amt - this.props.location.state.bill}
          />
        </div>
      </div>
    ) : (
      <div>Click here to move to home page</div>
    );
  }
}
export default Billpage;
