import React, { Component } from 'react';

class AddPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Name: '',
      Avail: '',
      Price: ''
    };
  }
  submit = () => {
    console.log(this.state);
  };
  change = e => {
    let { name, value } = e.target;
    this.setState({ [name]: value });
  };
  render() {
    return (
      <div className='center-align'>
        <div className='row'>
          <div className='input-field col s12 m7 l7'>
            <input
              type='text'
              name='Name'
              value={this.state.Name}
              onChange={this.change}
            />
            <label>Name</label>
          </div>
          <div className='input-field col s12 m7 l7'>
            <input
              type='number'
              name='Avail'
              value={this.state.Avail}
              onChange={this.change}
            />
            <label>Stock</label>
          </div>
          <div className='input-field col s12 m7 l7'>
            <input
              type='number'
              name='Price'
              value={this.state.Price}
              onChange={this.change}
            />
            <label>Price per one</label>
          </div>
        </div>
        <button
          class='btn waves-effect waves-light red lighten-2'
          type='submit'
          name='action'
          onClick={this.submit}
        >
          Submit
          <i class='material-icons left'>send</i>
        </button>
      </div>
    );
  }
}

export default AddPage;
