import React from 'react';
import { HashRouter, Route } from 'react-router-dom';
import { fetch } from './Fetches';
import Billpage from './Components/Pages/Billpage';
import Dashboard from './Components/Pages/Dashboard';
import EditPage from './Components/Pages/EditPage';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      products: this.fetchData
    };
  }
  check = () => {
    this.fetchData();
  };
  fetchData = async () => {
    let a = await fetch();
    await console.log('fetch_over');
    await a.forEach(async doc => {
      await this.state.products.push({ ...doc.data(), id: doc.id });
    });
    (await this.state.products.length) !== 0 &&
      this.setState({ isloading: '' });
    (await a.size) === 0 && this.setState({ isloading: 'serverError' });
    (await this.state.isloading) === 'serverError' && this.check();
  };
  render() {
    return (
      <HashRouter>
        <Route path='/' exact component={Dashboard} />
        <Route path='/billing...' component={Billpage} />
        <Route path='/edit' component={EditPage} exact />
      </HashRouter>
    );
  }
}
export default App;
